    size_t up = count(input.begin(), input.end(), '(');
    size_t down = count(input.begin(), input.end(), ')');
    cout << up << " " << down << endl;
    cout << "Answer: " << up - down << endl;
    
    // part 2
    
    int goingDown = 0;
    int goingUp = 0;
    int index = 1;
    for(string::iterator it = input.begin(); it != input.end(); ++it) {
        if (*it == '(') {
            goingUp++;
        } else {
            goingDown++;
        }
        
        if (goingDown > goingUp) {
            cout << "Answer: " << index << endl;
             
            break;
        }
        index++;
    }
